import { Injectable } from '@angular/core';

import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable';
import { firstValueFrom } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class PrinterPDFService {

  constructor(
  ) { }

  async generarPDFVerificacionEntradas(filtros: any, listaVerificaciones: any[]) {
  const doc = new jsPDF({ orientation: 'landscape' });

  doc.addImage(this.imgBase64, 'PNG', 20, 10, 20, 20);

  let line = 15;
  const titulo = 'Reporte de Verificación de Entradas';
  doc.text(titulo, 50, line);
  line += 10;

  doc.setFontSize(10);

  // Filtros
  const formatDate = (dateStr: string) => {
    if (!dateStr) return '';
    const d = new Date(dateStr);
    const day = String(d.getDate()).padStart(2, '0');
    const month = String(d.getMonth() + 1).padStart(2, '0');
    const year = d.getFullYear();
    return `${day}/${month}/${year}`;
  };

  if (filtros.startDate && filtros.endDate) {
    doc.text(
      `Rango de fechas: ${formatDate(filtros.startDate)} al ${formatDate(filtros.endDate)}`,
      50,
      line
    );
    line += 6;
  }
  if (filtros.noEntrada) {
    doc.text(`# Entrada: ${filtros.noEntrada}`, 50, line);
    line += 6;
  }
  if (filtros.productDesc) {
    doc.text(`Producto: ${filtros.productDesc}`, 50, line);
    line += 6;
  }
  if (typeof filtros.bPending !== 'undefined') {
    doc.text(`Pendiente: ${filtros.bPending ? 'Sí' : 'No'}`, 50, line);
    line += 6;
  }

  line += 2;

  autoTable(doc, {
    startY: line,
    head: [[
      '# Entrada', 'Código de barras', 'Nombre', 'Cantidad', 'Registrado por', 'Creado', 'Verificado', 'Mostrador', 'Activo'
    ]],
    body: listaVerificaciones.map((row: any) => [
      row.noEntrada,
      row.barCode,
      row.productName,
      row.cantidad,
      row.userName,
      row.createDate ? new Date(row.createDate).toLocaleDateString('es-MX') : '',
      row.firmaVer == 1 ? 'Sí' : 'No',
      row.firmaMost == 1 ? 'Sí' : 'No',
      row.active ? 'Sí' : 'No'
    ]),
    theme: 'grid',
    headStyles: { fontSize: 8 },
    bodyStyles: { fontSize: 7 },
    columnStyles: {
      3: { halign: 'right' }
    }
  });

  doc.save('ReporteVerificacionEntradas.pdf');
  const blobUrl = doc.output('bloburl');
  window.open(blobUrl, '_blank');
}

  imgBase64: any = 'data:image/png;base64,'; // tu imagen

}
